import React, { useState, useEffect } from 'react';
import Question from './Question';
import Score from './Score';

const quizQuestions = {
  general: [
    // ... add questions as described above
  ],
  science: [
    // ... add questions as described above
  ]
};

const Quiz = () => {
  const [category, setCategory] = useState('general');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [time, setTime] = useState(15); // 15 seconds per question

  useEffect(() => {
    const timer = setInterval(() => {
      setTime((prevTime) => prevTime - 1);
    }, 1000);

    if (time === 0) {
      handleAnswerOptionClick(false);
    }

    return () => clearInterval(timer);
  }, [time]);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizQuestions[category].length) {
      setCurrentQuestion(nextQuestion);
      setTime(15);
    } else {
      setShowScore(true);
    }
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setTime(15);
  };

  return (
    <div className='app'>
      <h1>Quiz App</h1>
      <label>
        Select Category:
        <select onChange={handleCategoryChange} value={category}>
          <option value="general">General Knowledge</option>
          <option value="science">Science</option>
        </select>
      </label>
      {showScore ? (
        <Score score={score} totalQuestions={quizQuestions[category].length} />
      ) : (
        <>
          <div>Time remaining: {time} seconds</div>
          <Question
            question={quizQuestions[category][currentQuestion]}
            handleAnswerOptionClick={handleAnswerOptionClick}
          />
        </>
      )}
    </div>
  );
};

export default Quiz;
